import { ADD_BOOK, BOOK_ERROR } from './types';

export const addBook = (book) => async (dispatch) => {
  try { 
      if (book) {
        console.log("Action " + JSON.stringify(book));
    } else {
        alert('Failed to add Book');
    }
    dispatch({
        type: ADD_BOOK,
        payload: book
    });
  }catch(error) {
    dispatch({
        type: BOOK_ERROR,
        payload: error
    }); 
  }
};
