import { ADD_BOOK, DELETE_BOOK } from './types';

export const addBook = (book) => ({ 
  type: ADD_BOOK,
  payload:book
});

export default function deleteBook (selectedBook)  {
 return { type: DELETE_BOOK,
  payload: selectedBook,}
}
