import React, { useState, useEffect } from "react";
import M from "materialize-css";
import '../styles/modal.css';
import { useDispatch } from 'react-redux';

function Modal (props) {
    useEffect(() => {
         const modalEnvoked = document.querySelectorAll('.modal');
            const options = {
                onOpenStart: null,
                onOpenEnd: null,
                onCloseStart: null,
                onCloseEnd: null,
                preventScrolling: true,
                inDuration: 250,
                outDuration: 250,
                opacity: 0.5,
                dismissible: false,
                startingTop: "4%",
                endingTop: "10%"
            };
            M.Modal.init(modalEnvoked, options);
      });
    
      const dispatch = useDispatch();
    const [name, setName] = useState("");
    const [price, setPrice] = useState(1.0);
    const [category, setCategory] = useState("");

    const onNameHandler = (event) => {
        setName(event.target.value);
    }

    const onPriceHandler = (event) => {
        // setPrice(event.currentTarget.value);
    }

    const onCategoryHandler = (event) => {
        // setCategory(event.currentTarget.value);
    }
  
    const onSubmitHandler = (event) => {
        event.preventDefault();

        let book = {
            name: name,
            price: price,
            category: category
        };
        console.log('modal' + book)
        dispatch(book);
        // props.history.push('/');
    }

        return (
          <div className="container">
            <div className="row">
                <a className="waves-effect waves-green btn-large modal-trigger" data-target="modal1">Add Book</a>
            </div>

            <div id="modal1" className="modal" onSubmit={onSubmitHandler}>
              <div className="modal-content">
                <h4>Add Book</h4>
                <div className="row-form">
                    <div className="input-field">
                                <label>Name</label>
                                <input type="text" value={name} onChange={onNameHandler} />
                    </div>
                        </div>
                    <div className="row-form">
                            <div className="input-field">
                                
                                <input type="number" step="0.01" value={price} onChange={onPriceHandler} /> 
                                <label>Price</label>
                            </div>
                    </div>
                    <div className="row-form">
                            <div className="input-field">
                                <label>Category</label>
                                <input type="text" value={category} onChange={onCategoryHandler} />
                            </div>
                    </div>
              </div>
              <div className="modal-footer">
                            <a type="submit" className="modal-close waves-effect waves-green btn-large">Submit</a>
              </div>
           
            </div>
        </div>
        );
      }
    
    
export default Modal;
