import { ADD_BOOK, DELETE_BOOK } from '../types';

const initialState = {booksArray: [{id: 0, name: 'Lord of the flies', price: 12.00, category: 'dystopian'}]};


function nextBookId(booksArray) {
    const maxId = booksArray.reduce((maxId, booksArray) => Math.max(booksArray.id, maxId), -1)
    return maxId + 1
}


export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_BOOK: 
            console.log("Book in reducer " + JSON.stringify([ ...state.booksArray, {id: nextBookId(state.booksArray),
            name: action.payload.name, price: action.payload.price, category: action.payload.category}]  ));
            console.log("Book inbnm,, " + JSON.stringify(...state.booksArray));
            return {...state, booksArray: [ ...state.booksArray, {id: nextBookId(state.booksArray),
            name: action.payload.name, price: action.payload.price, category: action.payload.category}]  }
            
        case DELETE_BOOK:
        //     const newEntities =  [...state.booksArray, {id: action.payload.id,
        //         name: action.payload.name, price: action.payload.price, category: action.payload.category}];
        // //    delete newEntities[action.payload.id, action.payload.name, action.payload.price, action.payload.category];
        //    console.log("Book g,, " + JSON.stringify(newEntities));
            return {
                //  ...state, booksArray: [ newEntities]  
                 ...state,
                booksArray:  [...state.booksArray, {id: action.payload.id,
                    name: action.payload.name, price: action.payload.price, category: action.payload.category}].filter((books) => books !== action.payload)
            }

            default: 
            return state;
    }
}
