import React from "react";
import "materialize-css/dist/css/materialize.min.css";
import Modal from "./modal";
import { connect, useDispatch } from "react-redux";
import '../styles/stylesApp.css';
import { deleteBook } from '../redux/actions';


let array;

function BookList (booksList, deleteBook) {
  array = booksList.booklist.booksArray;
  console.log("books in booksList component:  " + JSON.stringify(array));

function removeBook (id) {
  let book;
  array.some(function(obj) {
    if (obj.id === id) {
       return book = obj;
    }
  })
 
  console.log("book to delete " + JSON.stringify(book))

  if (book) {
    deleteBook = book;
  } else {
      console.log("There is no book");
  }

}

    return (
        <div>
          <Modal />

          <table className="centered highlight">
            <thead>
              <tr>
                  <th>Name</th>
                  <th>Price</th>
                  <th>Category</th>
                  <th>Manage</th>
              </tr>
            </thead>
            <tbody>
              {array && array.length ? array.map((array) =>  {
              return (<tr key={`booksList-${array.id}`}>
                <td>{array.name}</td>
                <td>{array.price}</td>
                <td>{array.category}</td>
                <td><a className="waves-effect  waves-light btn-small" id={array.id}>Update</a> 
                <div className="divider"/>
                <a className="waves-effect red waves-light btn-small" id={array.id} onClick={(e) => e = removeBook(array.id)}>Delete</a></td>
                </tr>);
              })
              : "No books, yay!"}
            </tbody>
         </table>
        </div>
    );
  }

  const mapStateToProps = (state) => {
    const  booksList  = state;
    return  booksList ;
  }


export default connect( null,  {deleteBook}, mapStateToProps )(BookList);
