import React from "react";
import "materialize-css/dist/css/materialize.min.css";
import Modal from "./modal";
import { connect } from "react-redux";



const BookList = (booksList) =>  {
  let array = booksList.booklist.booksArray;

  console.log("books:  " + JSON.stringify(array));
    return (
        <div>
          <Modal />

          <table class="centered responsive-table highlight">
            <thead>
              <tr>
                  <th>Name</th>
                  <th>Price</th>
                  <th>Category</th>
                  <th>Manage</th>
              </tr>
            </thead>
            <tbody>
              {array && array.length ? array.map((array) =>  {
                console.log('d' + array.id)
              return (<tr key={`booksList-${array.id}`}>
                <td>{array.name}</td>
                <td>{array.price}</td>
                <td>{array.category}</td>
                <td><a class="waves-effect  waves-light btn-small">Update</a> 
                <span></span>
                <a class="waves-effect red waves-light btn-small">Delete</a></td>
                </tr>);
              })
              : "No books, yay!"}
            </tbody>
         </table>
        </div>

        

    );
  }

  const mapStateToProps = (state) => {
    const  booksList  = state;
    return  booksList ;
  }

export default connect(mapStateToProps)(BookList);
