import React, { useState, useEffect} from "react";
import M from 'materialize-css/dist/js/materialize.min.js';
import { connect } from "react-redux";
import { addBook } from "../redux/actions";

function BookList (props)  {

    useEffect(() => {
        //initialize materialize
        M.init();
      });

    const [name, setName] = useState("");
    const [price, setPrice] = useState(1.0);
    const [category, setCategory] = useState("");

    const onNameHandler = (event) => {
        setName(event.currentTarget.value);
    }

    const onPriceHandler = (event) => {
        setPrice(event.currentTarget.value);
    }

    const onCategoryHandler = (event) => {
        setCategory(event.currentTarget.value);
    }


//     const onSubmit = () => {
//         if (name === '' || price === '' || category === '') {
//           M.toast({ html: 'Please Enter the First and Last Name' });
//         } else {
//           addBook({
//             name,
//             price,
//             category
//           });
    
//           M.toast({ html: `${name} was successfully added.` });
//         }
//       };
    
    return (
        <div>
                <div className="container">
                    <div className="row">
                        <button data-target="modalForm" class="btn modal-trigger waves-effect waves-light btn-large">Add Book</button>
                    </div>

                <div className="modal" id="modalForm">
                    <div className="modal-content">
                        <h4>Add Book</h4>
                        <div className="row">
                            <div className="input-field">
                                <label>Name</label>
                                <input type="text" value={name} onChange={onNameHandler} />
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field">
                                <label>Price</label>
                                <input type="number" step="0.01" value={price} onChange={onPriceHandler} /> 
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field">
                                <label>Category</label>
                                <input type="text" value={category} onChange={onCategoryHandler} />
                            </div>
                        </div>
                            <br />
                            <button type="submit" className="waves-effect waves-light btn-large">Add Book</button>
                        </div>
                    </div>
                </div>
        </div>

    );
  };


export default BookList;
