import React from "react";
import "materialize-css/dist/css/materialize.min.css";
import Modal from "./modal";
import { connect } from "react-redux";



const BookList = (booksList) =>  {
  console.log("books in booklist component:  " + JSON.stringify(booksList));
  
  let array = booksList.booklist.booksArray;
  console.log("books in booklist component:  " + JSON.stringify(array));
    return (
        <div>
          <Modal />

          <ul className="books-list">
              {array && array.length ? array.map(() =>  {
              return <li key={`booksList-${array.id}`}>{array.name}</li>;
              })
              : "No books, yay!"}
         </ul>
        </div>

        

    );
  }

  const mapStateToProps = (state) => {
    const  booksList  = state;
    return  booksList ;
  }

export default connect(mapStateToProps)(BookList);
