import { ADD_BOOK, BOOK_ERROR } from './types';


// export function addBook(book) {
export const addBook = (book) => async () => {

  if (book) {
      console.log("Action " + JSON.stringify(book));
  } else {
      alert('Failed to add Book');
  }
  return {
      type: ADD_BOOK,
      payload: book
  }
}


