import React, { useState, useEffect } from "react";
import M from "materialize-css";

function Modal () {
    useEffect(() => {
         const modalEnvoked = document.querySelectorAll('.modal');

            const options = {
                onOpenStart: () => {
                console.log("Open Start");
                },
                onOpenEnd: () => {
                console.log("Open End");
                },
                onCloseStart: () => {
                console.log("Close Start");
                },
                onCloseEnd: () => {
                console.log("Close End");
                },
                inDuration: 250,
                outDuration: 250,
                opacity: 0.5,
                dismissible: false,
                startingTop: "4%",
                endingTop: "10%"
            };
            M.Modal.init(modalEnvoked, options);
      });

    const [name, setName] = useState("");
    const [price, setPrice] = useState(1.0);
    const [category, setCategory] = useState("");

    const onNameHandler = (event) => {
        setName(event.currentTarget.value);
    }

    const onPriceHandler = (event) => {
        setPrice(event.currentTarget.value);
    }

    const onCategoryHandler = (event) => {
        setCategory(event.currentTarget.value);
    }

        return (
          <div>
            <a className="waves-effect waves-light btn modal-trigger" data-target="modal1">Add Book</a>
    
            <div id="modal1" className="modal">
              <div className="modal-content">
                <h4>Add Book</h4>
                <div className="row">
                    <div className="input-field">
                                <label>Name</label>
                                <input type="text" value={name} onChange={onNameHandler} />
                    </div>
                        </div>
                    <div className="row">
                            <div className="input-field">
                                
                                <input type="number" step="0.01" value={price} onChange={onPriceHandler} /> 
                                <label>Price</label>
                            </div>
                    </div>
                    <div className="row">
                            <div className="input-field">
                                <label>Category</label>
                                <input type="text" value={category} onChange={onCategoryHandler} />
                            </div>
                    </div>
              </div>
              <div className="modal-footer">
                            <a type="submit" className=" modal-close waves-effect waves-green btn-large" >Submit</a>
              </div>
           
            </div>
        </div>
        );
      }
    
    
export default Modal;
